use stream_protocol_lib::chunkers::json_growing_tree_chunker::JsonGrowingTreeChunker;

#[test]
fn test_json_chunk_iter() {
    use serde_json::json;

    let json_to_test = [
        (
            json!("abcdefghi"),
            [
                json!("ab"),
                json!("abcd"),
                json!("abcdef"),
                json!("abcdefgh"),
                json!("abcdefghi"),
            ].to_vec()
        ),
        (
            json!("東京都"), // buf_size being 2 bytes, but UTF8 graphemes being more than 2 bytes, we should have one at a time
            [
                json!("東"),
                json!("東京"),
                json!("東京都"),
            ].to_vec()
        ),
        (
            json!(["first","second"]),
            [
                json!([""]),
                json!(["fi"]),
                json!(["firs"]),
                json!(["first"]),
                json!(["first", ""]),
                json!(["first", "se"]),
                json!(["first", "seco"]),
                json!(["first", "second"]),
            ].to_vec()
        ),
        (
            json!({"first": "first", "second":"second"}),
            [
                json!({"first": ""}),
                json!({"first": "fi"}),
                json!({"first": "firs"}),
                json!({"first": "first"}),
                json!({"first": "first", "second": ""}),
                json!({"first": "first", "second": "se"}),
                json!({"first": "first", "second": "seco"}),
                json!({"first": "first", "second": "second"}),
            ].to_vec()
        ),
        (
            json!({"parent": {"first":["some", "array"], "second": 123, "third": true, "fourth": null, "fifth": {"a": "tail"}}}),
            [
                json!({"parent": {}}),
                json!({"parent": {"first": []}}),
                json!({"parent": {"first": [""]}}),
                json!({"parent": {"first": ["so"]}}),
                json!({"parent": {"first": ["some", ""]}}),
                json!({"parent": {"first": ["some", "ar"]}}),
                json!({"parent": {"first": ["some", "arra"]}}),
                json!({"parent": {"first": ["some", "array"], "second": 123}}),
                json!({"parent": {"first": ["some", "array"], "second": 123, "third": true}}),
                json!({"parent": {"first": ["some", "array"], "second": 123, "third": true, "fourth": null}}),
                json!({"parent": {"first": ["some", "array"], "second": 123, "third": true, "fourth": null, "fifth": {}}}),
                json!({"parent": {"first": ["some", "array"], "second": 123, "third": true, "fourth": null, "fifth": {"a": ""}}}),
                json!({"parent": {"first": ["some", "array"], "second": 123, "third": true, "fourth": null, "fifth": {"a": "ta"}}}),
                json!({"parent": {"first": ["some", "array"], "second": 123, "third": true, "fourth": null, "fifth": {"a": "tail"}}}),
            ].to_vec()
        )
    ];
    for (to_test, result) in json_to_test {
        let chunker = JsonGrowingTreeChunker::new(&to_test);
        let chunk_iter = chunker.chunks(2);
        for (chunk_idx, chunk) in chunk_iter.enumerate() {
            let chunk_value = chunk.borrow();
            assert!(chunk_idx < result.len());
            assert_eq!(*chunk_value, result[chunk_idx]);
        }
    }
}

