    for (to_test, result) in json_to_test {
        let chunker = JsonGrowingTreeChunker::new(&to_test);
        let chunk_iter = chunker.chunks(8);
        for (_chunk_idx, chunk) in chunk_iter.enumerate() {
            let chunk_value = chunk.borrow();
            assert_eq!(*chunk_value.to_string().as_str(), result[_chunk_idx].to_string());
        }
    }
